import { createStore } from "redux";
import api from "binance";
const binanceWS = new api.BinanceWS(true); // Argument specifies whether the responses should be beautified, defaults to true

binanceWS.onDepthUpdate('BNBBTC', (data) => {
    console.log(data);
});

const priceActions = {
    UPDATE_PRICE: "UPDATE_PRICE",
};

const priceReducer = (state, action) => {
    const newState = {...state}; //shallow copy is sufficient here
    switch (action.type) {
        case priceActions.UPDATE_PRICE:
            newState[action.pair] = action.price.toFixed(3);
            break;
        default:
            console.log("Invalid action type for priceReducer, got: ", action.type);
            break;
    }
};
const initialState = {}
const priceStore = createStore(priceReducer, initialState);
export default priceStore;
export { priceReducer };
