import React, {useEffect, useReducer, useContext, useState, createContext} from "react";
import socket from "../Socket";
import settingsStore from "../Store/Settings";

const anomaliesReducer = (state, action) => {
    state[action.type].push(...action.data);
    const LIMIT = 500;
    if (state[action.type].length > LIMIT) {
        state[action.type].shift();
    }

    // Do a deep copy here, so that on next comparison, react knows that the state has changed ({} !== {})
    return JSON.parse(JSON.stringify(state));
};

const AnomaliesContext = createContext();
const TimeContext = createContext(Date.now());
const SettingsContext = createContext(settingsStore.getState());

const DataDisplay = () => {
    const initialState = {
        "volume":       [],
        "price":        [],
        "liquidity":    [],
    };

    const [anomalies, dispatchAnomalies] = useReducer(anomaliesReducer, initialState);
    const [now, setNow] = useState(Date.now());
    const [settings, setSettings] = useState(settingsStore.getState());
    const dataTypes = ["volume", "price", "liquidity"];

    // Initialise the anomalies
    useEffect(() => {
        //Hit the cache api and set the initial state
        fetch("http://localhost:8080/api/little_cache").then(res => res.json().then(recentEventCache => {
            Object.keys(recentEventCache).forEach(key => {
                if (dataTypes.includes(key)) {
                    dispatchAnomalies({type: key, data: recentEventCache[key]});
                }
            })
        }));

        //Subscribe to anomalies to push to anomalies state
        socket.on("NEW_ANOMALY", (anomaly) => {
            dispatchAnomalies({type: anomaly.group, data: [anomaly]});
        });
    }, []);

    // Subscribe to the setting store
    useEffect(() => {
        return settingsStore.subscribe(() => {
            console.log(settingsStore.getState());
            setSettings(settingsStore.getState());
        });
    }, []);

    // Start setNow interval
    useEffect(() => {
        const threadId = setInterval(() => {
            setNow(Date.now());
        }, 1000);

        return () => clearInterval(threadId);
    }, []);

    const dataDivs = dataTypes.map((type, index) => <DataDiv dataType={type} key={index}/>);
    return (
        <React.Fragment>
            <SettingsContext.Provider value={settings}>
                <TimeContext.Provider value={now}>
                    <AnomaliesContext.Provider value={anomalies}>
                        {dataDivs}
                    </AnomaliesContext.Provider>
                </TimeContext.Provider>
            </SettingsContext.Provider>
        </React.Fragment>
    );
};

const DataDiv = (props) => {
    const anomalies = useContext(AnomaliesContext);
    const dataType = props.dataType;

    // Loop through all anomalies of that type and parse the anomaly into text
    const relevantAnomalies = anomalies[props.dataType].map((anomaly, index) => {
        return (
            <ParsedText dataType={dataType} anomaly={anomaly} key={index}/>
        );
    });

    return (
        <div id={`${dataType}_data_div`}>
            <h1>{`${dataType.slice(0,1).toUpperCase()}${dataType.slice(1)}`}</h1>
            <div>
                { //Anomalies are organised with oldest -> newest (needs to be reversed)
                  relevantAnomalies.reverse() }
            </div>
        </div>
    )
};

const ParsedText = ({anomaly, dataType}) => {
    let element = "";
    const settings = useContext(SettingsContext);
    const NOW = useContext(TimeContext);
    const timePassed = NOW - anomaly.data.timestamp * 1000;
    const movingTimeString = timePassed > 120 * 1000 ? timePassed > 60 * 1000 * 60 ? ""
                                                    : "(" + Math.floor(timePassed / 1000 / 60) + " minutes ago)"
                                                    : "(" + Math.floor(timePassed / 1000) + " seconds ago)";

    const anomalyTimeRange = anomaly.data.timestamp * 1000 - anomaly.data.limitTimestamp;
    const anomalyTimeRangeString = anomalyTimeRange > 120 * 1000 ? Math.floor(anomalyTimeRange / 1000 / 60) + " minutes " : Math.floor(anomalyTimeRange / 1000) + " seconds ";

    switch (anomaly.type) {
        case "volumeAnomaly":
            if (anomaly.data.total >= settings.minVolume) {
                element = <p className={`${dataType}_text`}>
                    {anomaly.data.total + " "} BTC worth of {anomaly.coin + " "}
                    was {anomaly.data.isBuyVolume ? "bought " : "sold "} on {anomaly.exchange + " "}
                    in the past {anomalyTimeRangeString} -
                    {" " + ((anomaly.data.total / anomaly.data.hourlyMa200 - 1) * 100).toFixed(2) + "% "} higher than the 200 hourly average.
                    {movingTimeString}
                </p>;
            }
            break;
        case "anomalyPriceRange":
            if (anomaly.data.change * 100 >= settings.minPriceIncrease) {
                element = <p className={`${dataType}_text`}>
                    {anomaly.coin} just {anomaly.data.positive ? "pumped " : "dumped "}
                    {(anomaly.data.change * 100).toFixed(2)}%
                    in the past {anomalyTimeRangeString} -
                    {" " + ((anomaly.data.range / anomaly.data.maRange - 1) * 100).toFixed(2) + "%"} higher than the 200 hourly average
                    {" " + movingTimeString}
                </p>
            }
            break;

        case "OrderBookIncrease":
            //console.log(settings.minOrderSize)
            if (anomaly.data.total >= settings.minOrderSize) {
                element = <p className={`${dataType}_text`}>
                    ${anomaly.data.total} {anomaly.data.isBuySide ? "buy " : "sell"} wall for {anomaly.coin + " "}
                    appeared {movingTimeString} on {anomaly.exchange} - A {((anomaly.data.buySide / anomaly.data.maTotal - 1) * 100).toFixed(2)}% increase
                    from the 200 hourly average
                </p>
            }
            break;
    }

    return (
        (() =>
            element ?
                    <div id={`${dataType}_data_1`} className={`data_box ${dataType}_box`}>
                        {element}
                        <p className="data_timestamp">{new Date(anomaly.data.timestamp * 1000).toGMTString()}</p>
                    </div>
                :
                    <React.Fragment/>
        )()
    );
};

export default DataDisplay;
