import { createStore } from "redux";

const actions = {
    UPDATE_MIN_VOLUME: "UPDATE_MIN_VOLUME",
    UPDATE_MIN_PRICE_INCREASE: "UPDATE_MIN_PRICE_INCREASE",
    UPDATE_MIN_ORDER_SIZE: "UPDATE_MIN_ORDER_SIZE",
}

const settingsReducer = (state, action) => {
    const newState = {...state}; // Cannot mutate the current state, must be a copy
    switch (action.type) {
        case actions.UPDATE_MIN_VOLUME:
            state.minVolume = action.data;
            break;
        case actions.UPDATE_MIN_PRICE_INCREASE:
            state.minPriceIncrease = action.data;
            break;
        case actions.UPDATE_MIN_ORDER_SIZE:
            state.minOrderSize = action.data;
            break;
        default:
            console.log("ERROR! '", action.type, "' is not an action");
            break;
    }

    return newState;
};

const settingsStore = createStore(settingsReducer);

export default settingsStore;
