import React, {useEffect, useState} from "react";
import priceStore from "./Store/Price";


const TopBar = () => {
    const [btcPrice, setBtcPrice] = useState(priceStore.getState()["BTCUSDT"]);
    const [isBuy, setIsBuy] = useState(true);

    useEffect(() => {
        priceStore.subscribe(() => {
            setBtcPrice(prevBtcPrice => {
                const newBtcPrice = priceStore.getState()["BTCUSDT"];
                setIsBuy(newBtcPrice > prevBtcPrice);
                return newBtcPrice;
            });
        });
    }, []);

    const priceColour = isBuy ? "#26A83A" : "red";
    return (
        <React.Fragment>
            <SearchForCoin/>
            <p id="top_bar_text">Want access to more data? Sign up for free!</p>
            <p id={"btcPriceFeed"}>BTCUSDT: <b style={{color: priceColour}}>${btcPrice}</b></p>
        </React.Fragment>
    );
};

const SearchForCoin = () => {
    const [searchValue, setSearchValue] = useState("");

    const handleSearchChange = event => {
        setSearchValue(event.target.value);
    };

    const predictionStyle = {
        position: "fixed"
    };

    return (
        <React.Fragment>
            <div>
                <input id="search_for_coin_input" placeholder="Search for coin"
                       value={searchValue} onChange={handleSearchChange}/>
                <div style={predictionStyle}>
                    <div>
                        Raven
                    </div>
                    <div>
                        Aion
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
};

export default TopBar;
