import React, {useEffect, useReducer, useContext, useState} from "react";

const anomaliesReducer = (state, action) => {
    state[action.type].push(...action.data);
    // Do a deep copy here, so that on next comparison, react knows that the state has changed ({} !== {})
    return JSON.parse(JSON.stringify(state));
};

const AnomaliesContext = React.createContext();
const TimeContext = React.createContext(Date.now());

const DataDisplay = () => {
    const initialState = {
        "volume":       [],
        "price":        [],
        "liquidity":    [],
    };

    const [anomalies, dispatchAnomalies] = useReducer(anomaliesReducer, initialState);
    const [now, setNow] = useState(Date.now());
    const dataTypes = ["volume", "price", "liquidity"];

    useEffect(() => {
        //Hit the cache api and set the initial state
        fetch("http://localhost:8080/api/little_cache").then(res => res.json().then(recentEventCache => {
            Object.keys(recentEventCache).forEach(key => {
                if (dataTypes.includes(key)) {
                    dispatchAnomalies({type: key, data: recentEventCache[key]});
                }
            })
        }));

        //Subscribe to anomalies
        //On new anomaly, determine the type then push to the anomalies state
    }, []);

    // Start setNow interval
    useEffect(() => {
        const threadId = setInterval(() => {
            setNow(Date.now());
        }, 1000);

        return () => clearInterval(threadId);
    }, []);

    const dataDivs = dataTypes.map((type, index) => <DataDiv dataType={type} key={index}/>);
    return (
        <React.Fragment>
            <TimeContext.Provider value={now}>
                <AnomaliesContext.Provider value={anomalies}>
                    {dataDivs}
                </AnomaliesContext.Provider>
            </TimeContext.Provider>
        </React.Fragment>
    );
};

const DataDiv = (props) => {
    const anomalies = useContext(AnomaliesContext);
    const dataType = props.dataType;

    // Loop through all anomalies of that type and parse the anomaly into text
    const relevantAnomalies = anomalies[props.dataType].map((anomaly, index) => {
        return (
            <div id={`${dataType}_data_1`} className={`data_box ${dataType}_box`} key={index}>
                <ParsedText dataType={dataType} anomaly={anomaly}/>
            </div>
        );
    });

    return (
        <div id={`${dataType}_data_div`}>
            <h1>{`${dataType.slice(0,1).toUpperCase()}${dataType.slice(1)}`}</h1>
            <div>
                {
                    //Anomalies are organised with oldest -> newest (needs to be reversed)
                    relevantAnomalies.reverse()
                }
            </div>
        </div>
    )
};

const ParsedText = props => {
    let element;
    const anomaly = props.anomaly;
    const NOW = useContext(TimeContext);
    const timePassed = NOW - anomaly.data.timestamp * 1000;
    const movingTimeString = timePassed > 120 * 1000 ? timePassed > 60 * 1000 * 60 ? ""
                                                    : "(" + Math.floor(timePassed / 1000 / 60) + " minutes ago)"
                                                    : "(" + Math.floor(timePassed / 1000) + " seconds ago)";

    const anomalyTimeRange = anomaly.data.timestamp * 1000 - anomaly.data.limitTimestamp;
    const anomalyTimeRangeString = anomalyTimeRange > 120 * 1000 ? Math.floor(anomalyTimeRange / 1000 / 60) + " minutes "
                                                           : Math.floor(anomalyTimeRange / 1000) + " seconds ";

    /// TODO: Add cases for all other anomalies, add the data on the backend for limitTimestamp (check it) and data on backend for exchange name
    switch (anomaly.type) {
        case "volumeAnomaly":
            element = <p className={`${props.dataType}_text`}>
                            ${anomaly.data.total + " "} of {anomaly.coin + " "}
                            was {anomaly.data.isBuyVolume ? "bought " : "sold "} on {anomaly.exchange + " "}
                            in the last {anomalyTimeRangeString} -
                            {" " + (anomaly.data.total / anomaly.data.hourlyMa200 * 100).toFixed(2) + "% "} higher than the 200 hourly average.
                            {movingTimeString}
                    </p>
    }

    return (
        <React.Fragment>
            {element}
            <p className="data_timestamp">{new Date(anomaly.data.timestamp * 1000).toGMTString()}</p>
        </React.Fragment>
    );
};

export default DataDisplay;
